mapper.select:
SELECT COUNT(*)
  FROM MEMBER
 WHERE MEMBER_ID = #{userId}

mapper.insert:
INSERT INTO MEMBER (MEMBER_NO
				  , MEMBER_ID
				  , MEMBER_PWD
				  , MEMBER_NAME
				  , EMAIL
				  , PHONE
				  , MEMBER_STATUS)
			VALUES (SEQ_MEMBER_NO.NEXTVAL
				  , #{memberId}
				  , #{memberPwd}
				  , #{memberName}
				  , #{email}
				  , #{phone}
				  , 'Y')

mapper.select:
SELECT *
FROM MEMBER
WHERE MEMBER_ID = #{memberId}
AND MEMBER_PWD = #{memberPwd}
AND MEMBER_STATUS = 'Y'

mapper.update:
UPDATE MEMBER
SET MEMBER_NAME = #{memberName}
  , EMAIL = #{email}
  , PHONE = #{phone}
WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER
SET MEMBER_STATUS = 'N'
WHERE MEMBER_ID = #{memberId}
AND MEMBER_PWD = #{memberPwd}

UPDATE MEMBER
SET MEMBER_STATUS = 'N'
WHERE MEMBER_NO = #{memberNo}
